// fadeIn
const selectAll = document.querySelectorAll('div');
selectAll.forEach(element => {
    if (!element.classList.contains('observed')) {
        element.style.opacity= '0';
        setTimeout(() => element.style.opacity = '1', 0);
    }
});
// fadeOut
document.querySelectorAll('[redirect]').forEach(element => {
    element.onclick = () => {
        const selectAll = document.querySelectorAll('div');
        selectAll.forEach(element => {
            if (!element.classList.contains('noRedirectTransition')) {
                element.style.opacity = '0';
            }
        });
        const link = element.dataset.href || element.getAttribute('redirect');
        setTimeout(() => location.assign(link) , 500);
    }
});