function lightboxOpen() {
    var vEvent = {
        keyCode: 38
    };
    fn_keydown(vEvent);
    document.getElementsByTagName('*')[0].style.overflow = 'hidden';
    document.getElementsByClassName('gridContainer')[0].style.pointerEvents = 'none';
    document.getElementsByClassName('lightbox')[0].style.display = 'block';
    document.getElementsByClassName('lightbox')[0].style.pointerEvents = 'all'
    setImage();
}
function lightboxClose() {
    document.getElementsByTagName('*')[0].style.overflow = 'visible';
    document.getElementsByClassName('gridContainer')[0].style.pointerEvents = 'all';
    document.getElementsByClassName('lightbox')[0].style.display = 'none';
    document.getElementsByClassName('lightbox')[0].style.pointerEvents = 'none';
    document.getElementsByClassName('lightboxSrc')[0].src = ''
}
