var imageIndex
function setImage() {
    document.getElementsByClassName('lightboxSrc')[0].src = images[imageIndex];
}
function cycleBackward() {
    imageIndex = (imageIndex + 27) % images.length;
    setImage();
}
function cycleForward() {
    imageIndex = (imageIndex + 1) % images.length;
    setImage();
}
window.onkeydown = fn_keydown;
function fn_keydown(event) {
    var lightboxSrc = document.getElementsByClassName('lightboxSrc')[0];
    if (event.keyCode === 37) {
        imageIndex = (imageIndex + 27) % images.length;
        setImage();
    } else if (event.keyCode === 39) {
        imageIndex = (imageIndex + 1) % images.length;
        setImage();
    }
}