const observerOptions={root:null,threshold:0,rootMargin:'0px 0px -150px 0px'};const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.remove('observed');entry.target.classList.add('observedVis');observer.unobserve(entry.target)}})},observerOptions);window.addEventListener('DOMContentLoaded',(event)=>{const sections=Array.from(document.getElementsByClassName('observed'));for(let section of sections){observer.observe(section)}});